import React, { useRef, useState, useEffect } from 'react';

const Contact = () => {
  let [success, setSuccess] = useState(false)
  let linkRef = useRef('johnobriendeveloper@gmail.com')

  return (
    <section
      className='contactContainer'
    >

      <div
        className='emailContainer'
      >
        <h2>Email</h2>
        <div>
          <span>johnobriendeveloper@gmail.com</span>
          <img
            className='copyClipboard'
            alt='copy clipboard' 
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              linkRef.current.select()
              document.execCommand('copy')
              setSuccess(true)

              setTimeout(() => {
                setSuccess(false)
              }, 1500)
            }}
          />
          <div
            className={
              success ? 'successMsg active' : 
              'sucessMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

      <div
        className=''
      >
        <h2>Phone</h2>
        <div>
          <span>johnobriendeveloper@gmail.com</span>
          <img
            className='copyClipboard'
            alt='copy clipboard' 
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              linkRef.current.select()
              document.execCommand('copy')
              setSuccess(true)

              setTimeout(() => {
                setSuccess(false)
              }, 1500)
            }}
          />
          <div
            className={
              success ? 'successMsg active' : 
              'sucessMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

    </section>
  )
}

export default Contact;
