import React, { useRef, useState, useEffect } from 'react';

const Contact = () => {
  let [emailSuccess, setEmailSuccess] = useState(false)
  let [phoneSuccess, setPhoneSuccess] = useState(false)
  let linkRef = useRef('johnobriendeveloper@gmail.com')
  let phoneRef = useRef('johnobriendeveloper@gmail.com')

  return (
    <section
      className='contactContainer'
    >

      <div
        className='emailContainer'
      >
        <h2>Email</h2>
        <div>
          <span>johnobriendeveloper@gmail.com</span>
          <textarea
            value={'johnobriendeveloper@gmail.com'}
            className='emailTextarea'
          ></textarea>
          <img
            className='copyClipboard'
            alt='copy clipboard' 
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              document.querySelect('.emailTextarea').execCommand('copy')
              setEmailSuccess(true)

              setTimeout(() => {
                setEmailSuccess(false)
              }, 1500)
            }}
          />
          <div
            className={
              emailSuccess ? 'successMsg active' : 
              'successMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

      <div
        className='phoneContainer'
      >
        <h2>Phone</h2>
        <div>
          <span className='phone'>+15088648618</span>
          <textarea
            value={'+15088648618'}
            className='phoneTextarea'
          ></textarea>
          <img
            className='copyClipboard'
            alt='copy clipboard' 
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              document.querySelect('.phoneTextarea').execCommand('copy')
              setPhoneSuccess(true)

              setTimeout(() => {
                setPhoneSuccess(false)
              }, 1500)
            }}
          />
          <div
            className={
              phoneSuccess ? 'successMsg active' : 
              'successMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

    </section>
  )
}

export default Contact;
