import React, { useRef, useState, useEffect } from 'react';

const Contact = () => {
  let [emailSuccess, setEmailSuccess] = useState(false)
  let [phoneSuccess, setPhoneSuccess] = useState(false)

  return (
    <section
      className='contactContainer'
    >

      <div
        className='emailContainer'
      >
        <h2>Email</h2>
        <div>
          <span>johnobriendeveloper@gmail.com</span>
          <textarea
            defaultValue={'johnobriendeveloper@gmail.com'}
            className='emailTextarea'
          ></textarea>
          <img
            className='copyClipboard'
            alt='copy clipboard'
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              navigator.clipboard.writeText('+15088648618')
                .then(() => {
                  setPhoneSuccess(true)

                  setTimeout(() => {
                    setPhoneSuccess(false)
                  }, 1500)
                })
            }}
          />
          <div
            className={
              emailSuccess ? 'successMsg active' : 
              'successMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

      <div
        className='phoneContainer'
      >
        <h2>Phone</h2>
        <div>
          <span className='phone'>+15088648618</span>
          <textarea
            defaultValue={'+15088648618'}
            className='phoneTextarea'
          ></textarea>
          <img
            className='copyClipboard'
            alt='copy clipboard' 
            src="https://img.icons8.com/material-outlined/64/000000/add-to-clipboard.png"
            onClick={() => {
              navigator.clipboard.writeText('+15088648618')
                .then(() => {
                  setPhoneSuccess(true)

                  setTimeout(() => {
                    setPhoneSuccess(false)
                  }, 1500)
                })
            }}
          />
          <div
            className={
              phoneSuccess ? 'successMsg active' : 
              'successMsg hidden'
            }
          >
            Copied
          </div>
        </div>
      </div>

    </section>
  )
}

export default Contact;
