import { gql } from '@apollo/client';
import AllPostQueryFragment from './all_posts_query_fragment.js';
const { ALL_POSTS, ALL_POSTS_ACTIVITY } = AllPostQueryFragment;

const Queries = {
  FETCH_USER_FEED: gql`
    query FetchUserFeed($query: String, $cursorId: String) {
      fetchUserFeed(query: $query, cursorId: $cursorId) {
        __typename
        ... on RepostType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          repostTrail {
            _id
            caption
            user {
              _id
              blogName
            }
            repost {
              _id
            }
          }
          repostedFrom {
            _id
            blogName
            kind
          }
          post {
            __typename
            ${ALL_POSTS}
          }
        }
        ${ALL_POSTS}
      }
    }
  `,
  FETCH_TAG_FEED: gql`
    query FetchTagFeed($query: String, $cursorId: String) {
      fetchTagFeed(query: $query, cursorId: $cursorId) {
        __typename
        ${ALL_POSTS}
      }
    }
  `,
  FETCH_USER_LIKES: gql`
    query FetchUserLikes($query: String) {
      fetchUserLikes(query: $query) {
        _id
        kind
        user {
          _id
          blogName
        }
        post {
          __typename
          ... on RepostType {
            _id
            kind
            post {
              __typename
              ${ALL_POSTS}
            }
          }
          ${ALL_POSTS}
        }
      }
    }
  `,
  FETCH_FOLLOWED_USERS: gql`
    query fetchFollowedUsers($query: String, $cursorId: String) {
      fetchFollowedUsers(query: $query, cursorId: $cursorId) {
        _id
        follows {
          __typename
          ... on UserType {
            _id
            blogName
            blogDescription
            kind
          }
        }
      }
    }
  `,
  FETCH_USER_FOLLOWERS: gql`
    query FetchUserFollowers($query: String, $cursorId: String) {
      fetchUserFollowers(query: $query, cursorId: $cursorId) {
        _id
        user {
          _id
          blogName
          blogDescription
        }
      }
    }
  `,
  FETCH_CURRENT_USER_BLOG: gql`
    query fetchUserBlog($query: String) {
      user(query: $query) {
        _id
        posts {
          __typename
          ${ALL_POSTS}
        }
      }
    }
  `,
  FETCH_USER_BLOG: gql`
    query fetchUserBlog($query: String) {
      user(query: $query) {
        _id
        blogName
        posts {
          __typename
          ... on RepostType {
            _id
            kind
            user {
              _id
              blogName
              profilePic {
                _id
                src
              }
            }
            repostTrail {
              _id
              caption
              user {
                _id
                blogName
              }
              repost {
                _id
              }
            }
            repostedFrom {
              _id
              blogName
              kind
            }
            post {
              __typename
              ${ALL_POSTS}
            }
          }
          ${ALL_POSTS}
        }
        reposts {
          post {
            __typename
            ${ALL_POSTS}
          }
        }
      }
    }
  `,
  FETCH_USER_DETAILS_COUNTS: gql`
    query FetchUserDetailsCounts($query: String) {
      user(query: $query) {
        _id
        blogName
        blogDescription
        email
        filteredTags
        filteredPostContent
        userFollowCount
        totalLikeCount
        followersCount
        userPostsCount
        profilePic {
          _id
          src
        }
      }
    }
  `,
  FETCH_USER_DETAILS: gql`
    query fetchUserDetails($query: String) {
      user(query: $query) {
        _id
        blogname
        userFollows {
          _id
          blogName
        }
        postLikes {
          _id
          post {
            __typename
            ${ALL_POSTS}
          }
        }
      }
    }
  `,
  FETCH_USER_FOLLOWED_TAGS: gql`
    query fetchUserAndFollowedTags($query: String) {
      user(query: $query) {
        _id
        tagFollows {
          _id
          title
        }
      }
    }
  `,
  SEARCH_USERS_AND_TAGS: gql`
    query SearchUsersAndTags($filter: UserAndTagInputType) {
      usersAndTags(filter: $filter) {
        __typename
        ... on UserType {
          _id
          blogName
          blogDescription
          email
          kind
        }
        ... on TagType {
          _id
          title
          kind
        }
      }
    }
  `,
  FETCH_MATCHING_TAGS: gql`
    query FetchMatchingTags($filter: String) {
      fetchMatchingTags(filter: $filter) {
        _id
        title
      }
    }
  `,
  FETCH_POST: gql`
    query FetchPost($query: ID) {
      post(query: $query) {
        __typename
        ... on RepostType {
          _id
          kind
          user {
            _id
            blogName
          }
          repostedFrom {
            _id
            blogName
          }
          repostTrail {
            _id
            caption
            user {
              _id
              blogName
            }
            repost {
              _id
            }
          }
          post {
            __typename
            ${ALL_POSTS}
          }
        }
        ${ALL_POSTS}
      }
    }
  `,
  FETCH_TAG: gql`
    query FetchTag($query: String) {
      tag(query: $query) {
        _id
        title
        followerCount
        followerHeatLastWeek
        postHeatLastWeek
      }
    }
  `,
  FETCH_LIKES_REPOSTS_AND_COMMENTS: gql`
  query FetchLikesRepostsAndComments($postId: ID) {
    fetchLikesRepostsAndComments(postId: $postId) {
        __typename
        ... on LikeType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
        }
        ... on RepostType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          repostedFrom {
            _id
            blogName
          }
          repostTrail {
            _id
            caption
            user {
              _id
            }
            repost {
              _id
            }
          }
        }
        ... on CommentType {
          _id
          kind
          content
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
        }
      }
    }
  `,
  FETCH_ALL_ACTIVITY: gql`
  query FetchAllUserActivity($query: String, $cursorId: String) {
    fetchAllUserActivity(query: $query, cursorId: $cursorId) {
        __typename
        ... on MentionType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          post {
            __typename
            ... on RepostType {
              _id
              kind
              repostTrail {
                _id
                caption
                user {
                  _id
                }
                repost {
                  _id
                }
              }
              post {
                __typename
                ${ALL_POSTS_ACTIVITY}
              }
            }
            ${ALL_POSTS_ACTIVITY}
          }
          createdAt
        }
        ... on RepostType {
          _id
          kind
          repostTrail {
            _id
            caption
            user {
              _id
            }
            repost {
              _id
            }
          }
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          repostedFrom {
            _id
            blogName
          }
          post {
            __typename
            ${ALL_POSTS_ACTIVITY}
          }
          createdAt
        }
        ... on CommentType {
          _id
          kind
          content
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          post {
            __typename
            ... on RepostType {
              _id
              kind
              user {
                _id
                blogName
              }
              post {
                __typename
                ${ALL_POSTS_ACTIVITY}
              }
            }
            ${ALL_POSTS_ACTIVITY}
          }
          createdAt
        }
        ... on CommentType {
          _id
          kind
          content
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          post {
            __typename
            ... on RepostType {
              _id
              kind
              post {
                __typename
                ${ALL_POSTS_ACTIVITY}
              }
            }
            ${ALL_POSTS_ACTIVITY}
          }
          createdAt
        }
        ... on FollowType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
          follows {
            __typename
            ... on TagType {
              _id
            }
            ... on UserType {
              _id
              blogName
            }
          }
          createdAt
        }
        ... on LikeType {
          _id
          kind
          user {
            _id
            blogName
            profilePic {
              _id
              src
            }
          }
